const controller = require('controller');
const callArr = {
    '/' : 'home',
    '/about' : 'about',
    '/contacts' : 'contacts',
}

module.exports.getResponse = (url, res) => {
        var found = false;
        res.setHeader('Content-type','text/html');
        if(url in callArr){
            controller[callArr[url]](res);
            found = true;
        }
        if(!found) controller['notFound'](res);
        res.end();
}