const controller = require('controller');

module.exports = class Router{
    constructor(req, res){
        this.req = req;
        this.res = res;
        this.callArr = {
            '/' : 'home',
            '/about' : 'about',
            '/contacts' : 'contacts',
        }
    }

    getResponse(){
        this.res.setHeader('Content-type','text/html');
        for(var property in this.callArr){
            if(property == this.req.url){
                controller[this.callArr[property]](this.res);
            }
        }
        this.res.end();
    }
}